var binhex={convertBinaryToHexadecimal:function(r){for(var n="",t=0;t<r.length;t+=4){var e=r.substr(t,4),a=parseInt(e,2),i=a.toString(16);n+=i.toUpperCase()}return n},convertHexadecimalToBinary:function(r){for(var n="",t=0;t<r.length;t++){var e=parseInt(r.charAt(t),16),a=this.leftPadding(e.toString(2),"0",4);n+=a}return n},leftPadding:function(r,n,t){if(!r||!n||r.length>=t)return r;for(var e=(t-r.length)/n.length,a=0;e>a;a++)r=n+r;return r}};